// src/components/PrintableGamesPage.jsx
import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import WeekSelector from './WeekSelector';
import printIcon from '../assets/print-icon.png';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const contentRef = useRef();

  useEffect(() => {
    fetch(`http://localhost:4000/api/games/${weekNumber}`)
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error('Error fetching games:', err));
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    window.print();
  };

  const handleExportPDF = () => {
    const element = contentRef.current;
    const opt = {
      margin:       0.5,
      filename:     `The_Spread_Week_${weekNumber}.pdf`,
      image:        { type: 'jpeg', quality: 0.98 },
      html2canvas:  { scale: 2 },
      jsPDF:        { unit: 'in', format: 'letter', orientation: 'portrait' }
    };
    html2pdf().set(opt).from(element).save();
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Georgia, serif' }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
        <img src={printIcon} alt="Print Icon" style={{ width: '40px', marginRight: '10px' }} />
        <h1 style={{ margin: 0 }}>The Spread â€“ Week {weekNumber}</h1>
      </div>

      <div style={{ marginBottom: '10px' }}>
        <select value={weekNumber} onChange={handleWeekChange}>
          {Array.from({ length: 18 }, (_, i) => (
            <option key={i + 1} value={i + 1}>Week {i + 1}</option>
          ))}
        </select>
        <button onClick={handlePrint} style={{ marginLeft: '10px' }}>Print Page</button>
        <button onClick={handleExportPDF} style={{ marginLeft: '10px' }}>Export to PDF</button>
      </div>

      <div ref={contentRef}>
        <ol>
          {games.map((game, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              <strong>
                {game.favorite} ({game.spread > 0 ? `+${game.spread}` : game.spread}) vs {game.underdog}
              </strong>
              <br />
              <span>
                Date: {game.date} | Time: {game.time}
              </span>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
};

export default PrintableGamesPage;
