// src/components/PrintableGamesPage.jsx

import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import printIcon from '../assets/print-icon.png';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const contentRef = useRef();

  useEffect(() => {
    if (!weekNumber) return;

    fetch(`http://localhost:4000/api/games/${weekNumber}`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setGames(data);
        } else {
          console.error("Data is not an array:", data);
          setGames([]);
        }
      })
      .catch((err) => console.error('Error fetching games:', err));
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    window.print();
  };

  const handleExportToPDF = () => {
    if (contentRef.current) {
      html2pdf().from(contentRef.current).save(`Week_${weekNumber}_Games.pdf`);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Printable Game List - Week {weekNumber}</h2>

      <div>
        <label>Select Week:&nbsp;</label>
        <select value={weekNumber} onChange={handleWeekChange}>
          <option value="1">Week 1</option>
          <option value="2">Week 2</option>
        </select>
        &nbsp;
        <button onClick={handleExportToPDF}>
          <img src={printIcon} alt="Export to PDF" style={{ width: '20px', marginRight: '5px' }} />
          Export to PDF
        </button>
        &nbsp;
        <button onClick={handlePrint}>Print This Page</button>
      </div>

      <div ref={contentRef} style={{ marginTop: '20px' }}>
        {games.length === 0 ? (
          <p>No games found for Week {weekNumber}.</p>
        ) : (
          <ul>
            {games.map((game, index) => (
              <li key={index}>
                <strong>{game.date}</strong> at <strong>{game.time}</strong> â€” {game.team1} vs. {game.team2} ({game.spread})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PrintableGamesPage;
