import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const PrintableGamesPage = () => {
  const { week } = useParams();
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [weeksAvailable, setWeeksAvailable] = useState([]);

  useEffect(() => {
    fetch(`/api/games/${week}`)
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => {
        console.error('Error loading games:', err);
        setGames([]);
      });
  }, [week]);

  useEffect(() => {
    const maxWeeks = 18;
    const available = [];
    for (let i = 1; i <= maxWeeks; i++) {
      available.push(i);
    }
    setWeeksAvailable(available);
  }, []);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <div className="p-8 max-w-3xl mx-auto font-sans print:text-black">
      <div className="flex justify-between items-center mb-6 print:hidden">
        <h1 className="text-3xl font-bold">üñ®Ô∏è Week {week} Games</h1>
        <div className="flex gap-3">
          <select
            value={week}
            onChange={handleWeekChange}
            className="border px-3 py-1 rounded"
          >
            {weeksAvailable.map((w) => (
              <option key={w} value={w}>
                Week {w}
              </option>
            ))}
          </select>
          <button
            onClick={handlePrint}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            üñ®Ô∏è Print Page
          </button>
        </div>
      </div>

      <h2 className="text-2xl font-semibold text-center mb-4 print:block hidden">
        Week {week} Games
      </h2>

      {games.length === 0 ? (
        <p className="text-center text-gray-500 italic">No games found for this week.</p>
      ) : (
        <ol className="list-decimal list-inside space-y-4">
          {games.map((game, index) => (
            <li key={index} className="text-lg">
              <div>
                <strong>{game.team1} ({game.spread})</strong> vs <strong>{game.team2}</strong>
              </div>
              <div className="ml-4 text-sm text-gray-700">
                Date: {game.date} | Time: {game.time}
              </div>
            </li>
          ))}
        </ol>
      )}
    </div>
  );
};

export default PrintableGamesPage;
