import React, { useState } from 'react';

const AdminPicksPage = () => {
  const [selectedWeek, setSelectedWeek] = useState('1');
  const [winningTeams, setWinningTeams] = useState(Array(10).fill(''));
  const [statusMessage, setStatusMessage] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleTeamChange = (index, value) => {
    const updatedTeams = [...winningTeams];
    updatedTeams[index] = value;
    setWinningTeams(updatedTeams);
  };

  const handleSave = () => {
    fetch('http://localhost:4000/api/save-winners', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        week: selectedWeek,
        winners: winningTeams,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to save winners');
        }
        return response.json();
      })
      .then((data) => {
        setStatusMessage(data.message);
        setSubmitted(true);
      })
      .catch((error) => {
        console.error('Error saving winners:', error);
        setStatusMessage('Error saving winners.');
      });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Weekly Scoring</h2>
      <label><b>Select Week:</b>&nbsp;</label>
      <select value={selectedWeek} onChange={(e) => setSelectedWeek(e.target.value)}>
        {[...Array(18)].map((_, i) => (
          <option key={i + 1} value={i + 1}>Week {i + 1}</option>
        ))}
      </select>

      <div style={{ marginTop: '20px' }}>
        <label><b>Enter 10 Winning Teams:</b></label>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', marginTop: '10px' }}>
          {winningTeams.map((team, index) => (
            <input
              key={index}
              type="text"
              value={team}
              onChange={(e) => handleTeamChange(index, e.target.value)}
              style={{ width: '120px' }}
            />
          ))}
        </div>
      </div>

      <button onClick={handleSave} style={{ marginTop: '15px', padding: '5px 15px', backgroundColor: 'green', color: 'white' }}>
        Save Winners
      </button>

      <div style={{ marginTop: '10px', color: statusMessage.includes('Error') ? 'red' : 'green' }}>
        {statusMessage}
      </div>

      {submitted && (
        <div style={{ marginTop: '10px' }}>
          <i>No picks submitted for Week {selectedWeek}.</i>
        </div>
      )}
    </div>
  );
};

export default AdminPicksPage;
