import React, { useState, useEffect } from 'react';

const ChatPage = () => {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);

  // Load chat messages from backend on mount
  useEffect(() => {
    fetch('http://localhost:4000/chat')
      .then(res => res.json())
      .then(data => setChatLog(data))
      .catch(err => {
        console.error('Failed to load chat:', err);
        alert('Unable to load chat messages.');
      });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim() || !message.trim()) {
      alert('Please enter both your name and message.');
      return;
    }

    const newMessage = {
      name,
      message,
      timestamp: new Date().toLocaleString()
    };

    try {
      const response = await fetch('http://localhost:4000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newMessage)
      });

      const result = await response.json();

      if (response.ok) {
        setChatLog([newMessage, ...chatLog]);
        setMessage('');
      } else {
        alert(result.error || 'Failed to send message.');
      }
    } catch (err) {
      console.error('Failed to post message:', err);
      alert('Failed to post your message.');
    }
  };

  return (
    <div style={{ padding: '30px', maxWidth: '700px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>üí¨ Pick'em Chat Room</h2>
      <p>Say hello, talk a little smack, or celebrate your winning week! üèÜ</p>

      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ width: '40%', padding: '8px', marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Your Message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={{ width: '40%', padding: '8px', marginRight: '10px' }}
        />
        <button type="submit">Send</button>
      </form>

      <div>
        {chatLog.length === 0 ? (
          <p>No messages yet. Be the first to say something!</p>
        ) : (
          chatLog.map((entry, index) => (
            <div key={index} style={{ borderBottom: '1px solid #ccc', padding: '10px 0' }}>
              <strong>{entry.name}</strong>{' '}
              <em style={{ fontSize: '0.8em' }}>({entry.timestamp})</em>
              <p>{entry.message}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ChatPage;
