// src/components/PrintableGamesPage.jsx
import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import printIcon from '../assets/print-icon.png';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams(); // <-- this is what we rely on
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const contentRef = useRef();

  useEffect(() => {
    if (!weekNumber) return;

    fetch(`http://localhost:4000/api/games/${weekNumber}`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setGames(data);
        } else {
          console.error('Data is not an array:', data);
          setGames([]);
        }
      })
      .catch((err) => {
        console.error('Error fetching games:', err);
        setGames([]);
      });
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    window.print();
  };

  const handleExportToPDF = () => {
    if (contentRef.current) {
      html2pdf().from(contentRef.current).save(`Week_${weekNumber}_Game_List.pdf`);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Printable Game List - Week {weekNumber}</h2>

      <label htmlFor="weekSelect">Select Week: </label>
      <select id="weekSelect" onChange={handleWeekChange} value={weekNumber}>
        {Array.from({ length: 18 }, (_, i) => (
          <option key={i + 1} value={i + 1}>
            Week {i + 1}
          </option>
        ))}
      </select>

      <button onClick={handleExportToPDF}>
        <img src={printIcon} alt="Print Icon" style={{ width: '16px', marginRight: '5px' }} />
        Export to PDF
      </button>

      <button onClick={handlePrint} style={{ marginLeft: '10px' }}>
        Print This Page
      </button>

      <div ref={contentRef} style={{ marginTop: '20px' }}>
        {games.length === 0 ? (
          <p>No games found for Week {weekNumber}.</p>
        ) : (
          <ul>
            {games.map((game, index) => (
              <li key={index}>
                {game.team1} vs {game.team2} ({game.spread})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PrintableGamesPage;
