// src/components/AdminUploadPage.jsx

import React, { useState } from 'react';

const AdminUploadPage = () => {
  const [selectedWeek, setSelectedWeek] = useState('2'); // latest first
  const [gameData, setGameData] = useState('');
  const [winnerData, setWinnerData] = useState('');
  const [message, setMessage] = useState('');

  const handleGameUpload = async () => {
    try {
      const res = await fetch(`http://localhost:4000/api/upload-games/${selectedWeek}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: gameData,
      });
      const result = await res.json();
      setMessage(`Games uploaded for week ${selectedWeek}: ${result.message || 'Success'}`);
    } catch (error) {
      console.error(error);
      setMessage('Failed to upload games.');
    }
  };

  const handleWinnerUpload = async () => {
    try {
      const res = await fetch(`http://localhost:4000/api/upload-winners/${selectedWeek}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: winnerData,
      });
      const result = await res.json();
      setMessage(`Winners uploaded for week ${selectedWeek}: ${result.message || 'Success'}`);
    } catch (error) {
      console.error(error);
      setMessage('Failed to upload winners.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>Admin Upload Page</h2>

      <label>
        Select Week:
        <select value={selectedWeek} onChange={(e) => setSelectedWeek(e.target.value)}>
          {[...Array(18)].map((_, i) => {
            const week = 18 - i; // reverse order
            return (
              <option key={week} value={week}>
                Week {week}
              </option>
            );
          })}
        </select>
      </label>

      <div style={{ marginTop: '20px' }}>
        <h3>Upload Game Data</h3>
        <textarea
          rows="8"
          cols="60"
          placeholder='Paste JSON-formatted game data here'
          value={gameData}
          onChange={(e) => setGameData(e.target.value)}
        />
        <br />
        <button onClick={handleGameUpload}>Upload Games</button>
      </div>

      <div style={{ marginTop: '30px' }}>
        <h3>Upload Winner Data</h3>
        <textarea
          rows="5"
          cols="60"
          placeholder='Paste JSON-formatted winner data here'
          value={winnerData}
          onChange={(e) => setWinnerData(e.target.value)}
        />
        <br />
        <button onClick={handleWinnerUpload}>Upload Winners</button>
      </div>

      {message && <p style={{ marginTop: '20px', color: 'green' }}>{message}</p>}
    </div>
  );
};

export default AdminUploadPage;
