import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import WeekSelector from "./WeekSelector";

const ResultsPage = () => {
  const { weekNumber } = useParams();
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const response = await fetch(`/api/scores/${weekNumber}`);
        if (!response.ok) {
          throw new Error("Failed to fetch results");
        }
        const data = await response.json();
        setResults(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchResults();
  }, [weekNumber]);

  if (loading) return <div className="p-4">Loading results...</div>;
  if (error) return <div className="p-4 text-red-600">Error: {error}</div>;

  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-4 text-center">
        Results - Week {weekNumber}
      </h2>

      {/* Week Selector Dropdown */}
      <WeekSelector maxWeek={18} />

      <table className="w-full border-collapse text-sm sm:text-base mt-4">
        <thead>
          <tr className="bg-gray-200 border-b">
            <th className="p-3 text-left">Player</th>
            <th className="p-3 text-left">Picks</th>
            <th className="p-3 text-left text-green-800">Correct Picks</th>
            <th className="p-3 text-center text-blue-800">Weekly Score</th>
            <th className="p-3 text-center text-purple-800">Total Score</th>
            <th className="p-3 text-center text-yellow-600">Praise</th>
          </tr>
        </thead>
        <tbody>
          {results.map((player, index) => (
            <tr
              key={index}
              className={`${
                player.weeklyScore >= 9
                  ? "bg-yellow-100 border-2 border-yellow-400"
                  : index % 2 === 0
                  ? "bg-white"
                  : "bg-gray-50"
              }`}
            >
              <td className="p-3 font-semibold">{player.name}</td>
              <td className="p-3 whitespace-pre-wrap">{player.picks.join(", ")}</td>
              <td className="p-3 whitespace-pre-wrap text-green-700">
                {player.correctPicks.join(", ")}
              </td>
              <td className="p-3 font-bold text-blue-800 text-center">
                {player.weeklyScore}
              </td>
              <td className="p-3 font-bold text-purple-700 text-center">
                {player.totalScore}
              </td>
              <td className="p-3 text-yellow-600 text-center text-xl">
                {player.praise || "-"}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ResultsPage;
