import React, { useEffect, useState } from "react";

const CumulativeResultsPage = () => {
  const [cumulativeResults, setCumulativeResults] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/api/scores/summary")
      .then((res) => res.json())
      .then((data) => {
        // Sort players by score descending
        const sorted = data.sort((a, b) => b.totalScore - a.totalScore);
        setCumulativeResults(sorted);
      })
      .catch((err) => console.error("Error fetching summary:", err));
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">üèÜ Cumulative Scores</h1>
      <table className="border-collapse border">
        <thead>
          <tr>
            <th className="border px-2">#</th>
            <th className="border px-2">Player</th>
            <th className="border px-2">Total Score</th>
          </tr>
        </thead>
        <tbody>
          {cumulativeResults.map((player, index) => (
            <tr key={player.name}>
              <td className="border text-center px-2">{index + 1}</td>
              <td className="border px-2">{player.name}</td>
              <td className="border text-center px-2">{player.totalScore}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CumulativeResultsPage;
