import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PrintableGamesPage = () => {
  const { week } = useParams();
  const [games, setGames] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:4000/api/games/${week}`)
      .then(res => res.json())
      .then(data => setGames(data))
      .catch(err => console.error('Error loading games:', err));
  }, [week]);

  return (
    <div className="max-w-3xl mx-auto p-4 text-center">
      <h1 className="text-2xl font-bold mb-4">Printable Picks Sheet - Week {week}</h1>
      <div className="space-y-2 text-left">
        {games.map((game, idx) => {
          const spread = parseFloat(game.spread);
          const isFavorite = spread < 0;
          const favorite = isFavorite ? game.team1 : game.team2;
          const underdog = isFavorite ? game.team2 : game.team1;
          const displaySpread = Math.abs(spread).toFixed(1);

          return (
            <div key={idx} className="border-b pb-2">
              {favorite} (-{displaySpread}) vs {underdog} (+{displaySpread})
            </div>
          );
        })}
      </div>
      <p className="mt-6 text-gray-500 italic">Print and circle your picks above.</p>
    </div>
  );
};

export default PrintableGamesPage;
