import React, { useEffect, useState } from 'react';

const AllPlayersPicksPage = ({ currentWeek }) => {
  const [playerData, setPlayerData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/api/player-picks/${currentWeek}`)
      .then(res => res.json())
      .then(data => {
        setPlayerData(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error loading player picks:', err);
        setPlayerData({});
        setLoading(false);
      });
  }, [currentWeek]);

  if (loading) return <div style={{ padding: '20px' }}>Loading All Player Picks...</div>;

  if (!playerData || typeof playerData !== 'object') {
    return <div style={{ padding: '20px' }}>No data to display.</div>;
  }

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        All Players' Picks - Week {currentWeek}
      </h2>

      {Object.entries(playerData).map(([player, data]) => {
        if (!data || !Array.isArray(data.picks)) return null;

        return (
          <div key={player} style={{ border: '1px solid #ccc', borderRadius: '8px', marginBottom: '16px', padding: '12px' }}>
            <h3 style={{ marginBottom: '8px', fontWeight: 'bold' }}>{player}</h3>
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
              {data.picks.map((pick, idx) => (
                <div
                  key={idx}
                  style={{
                    padding: '6px 10px',
                    borderRadius: '4px',
                    backgroundColor: pick.isCorrect ? '#d4edda' : '#f8d7da',
                    color: pick.isCorrect ? '#155724' : '#721c24',
                    border: `1px solid ${pick.isCorrect ? '#c3e6cb' : '#f5c6cb'}`,
                    fontWeight: '500'
                  }}
                >
                  {pick.team}
                </div>
              ))}
            </div>
            <div style={{ marginTop: '8px', fontSize: '14px' }}>
              âœ… Correct Picks: <strong>{data.weekly}</strong><br />
              ðŸ§® Total Points: <strong>{data.total}</strong>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AllPlayersPicksPage;
