import React, { useEffect, useState } from 'react';

function PotPage() {
  const [potData, setPotData] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('/data/pot.json')
      .then(res => {
        if (!res.ok) throw new Error('Failed to load pot data');
        return res.json();
      })
      .then(data => setPotData(data))
      .catch(err => {
        console.error(err);
        setError('Unable to load pot data.');
      });
  }, []);

  return (
    <div style={{ padding: '40px', textAlign: 'center' }}>
      <h1 style={{ fontSize: '2em', color: '#004d00' }}>üèÜ Weekly Pot Totals</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div style={{ marginTop: '30px', display: 'inline-block', textAlign: 'left' }}>
        {Object.keys(potData).map(week => (
          <div
            key={week}
            style={{
              background: '#e8ffe8',
              border: '1px solid #88cc88',
              borderRadius: '10px',
              padding: '12px 20px',
              marginBottom: '10px',
              fontSize: '1.2em',
              minWidth: '280px',
              boxShadow: '2px 2px 8px rgba(0,0,0,0.1)'
            }}
          >
            <strong>Week {week}</strong>: ${potData[week].toLocaleString()}
          </div>
        ))}
      </div>
    </div>
  );
}

export default PotPage;
