import React, { useEffect, useState } from 'react';

const ResultsPage = () => {
  const [results, setResults] = useState([]);
  const [cumulativeScores, setCumulativeScores] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState(1); // default to Week 1
  const [weeksAvailable, setWeeksAvailable] = useState([]);

  useEffect(() => {
    fetch(`/api/scores/${selectedWeek}`)
      .then((res) => res.json())
      .then((data) => setResults(data))
      .catch((err) => console.error('Error fetching weekly results:', err));
  }, [selectedWeek]);

  useEffect(() => {
    fetch(`/api/cumulative-scores`)
      .then((res) => res.json())
      .then((data) => setCumulativeScores(data))
      .catch((err) => console.error('Error fetching cumulative scores:', err));
  }, []);

  useEffect(() => {
    // Dynamically figure out how many weeks are available based on scores
    const maxWeeks = 18; // adjust this as needed
    const available = [];
    for (let i = 1; i <= maxWeeks; i++) {
      available.push(i);
    }
    setWeeksAvailable(available);
  }, []);

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">Results for Week {selectedWeek}</h2>

      <div className="mb-4 text-center">
        <label className="mr-2 font-semibold">Select Week:</label>
        <select
          className="border px-2 py-1 rounded"
          value={selectedWeek}
          onChange={(e) => setSelectedWeek(Number(e.target.value))}
        >
          {weeksAvailable.map((week) => (
            <option key={week} value={week}>
              Week {week}
            </option>
          ))}
        </select>
      </div>

      <div className="mb-8">
        {results.length === 0 ? (
          <p className="text-center text-gray-500">No picks submitted for this week yet.</p>
        ) : (
          results.map((player, index) => (
            <div
              key={index}
              className="border rounded-lg p-4 mb-4 shadow-sm bg-white"
            >
              <h3 className="text-lg font-semibold mb-2">
                {player.playerName} — Score: {player.score}/10{' '}
                {player.score >= 9 && (
                  <span className="text-yellow-500 font-bold ml-2">⭐ Awesome Picks!</span>
                )}
              </h3>
              <ul className="list-disc list-inside ml-2">
                {player.picks.map((pick, idx) => (
                  <li key={idx} className={pick.correct ? 'text-green-600' : 'text-red-600'}>
                    {pick.team} {pick.correct ? '(Correct)' : '(Wrong)'}
                  </li>
                ))}
              </ul>
            </div>
          ))
        )}
      </div>

      <div className="mt-8">
        <h2 className="text-xl font-bold mb-2 text-center">🏆 Cumulative Scores</h2>
        <table className="w-full table-auto border-collapse">
          <thead>
            <tr>
              <th className="border px-4 py-2">Player</th>
              <th className="border px-4 py-2">Total Score</th>
            </tr>
          </thead>
          <tbody>
            {cumulativeScores.map((player, idx) => (
              <tr key={idx}>
                <td className="border px-4 py-2">{player.playerName}</td>
                <td className="border px-4 py-2">{player.totalScore}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ResultsPage;
