import React, { useEffect, useState, useRef } from 'react';

const SpreadPage = () => {
  const [games, setGames] = useState([]);
  const printRef = useRef();

  useEffect(() => {
    fetch('http://localhost:4000/data/games_week_1.json')
      .then(res => res.json())
      .then(data => setGames(data))
      .catch(() => setGames([]));
  }, []);

  const handlePrint = () => {
    window.print();
  };

  const handleExportPDF = () => {
    import('jspdf').then(jsPDF => {
      const doc = new jsPDF.jsPDF();
      doc.setFontSize(12);
      doc.text('Current Week Game Spread', 14, 20);

      let y = 30;
      doc.setFont('courier', 'normal');
      games.forEach(game => {
        const line = `${game.date || '---'}   ${game.time}   ${game.team1} vs ${game.team2}`;
        doc.text(line, 14, y);
        y += 10;
      });

      doc.save('spread.pdf');
    });
  };

  return (
    <div style={{ padding: '30px', maxWidth: '800px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>The Spread by Week</h2>
      <p>Below is the current week‚Äôs game schedule. You can print or export this page for reference.</p>

      <div style={{ marginBottom: '20px' }}>
        <button onClick={handlePrint} style={{ marginRight: '10px' }}>üñ®Ô∏è Print</button>
        <button onClick={handleExportPDF}>üìÑ Export to PDF</button>
      </div>

      <div ref={printRef}>
        <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: '16px' }}>
          <thead>
            <tr style={{ borderBottom: '2px solid #000' }}>
              <th style={{ textAlign: 'left' }}>Date</th>
              <th style={{ textAlign: 'left' }}>Time</th>
              <th style={{ textAlign: 'left' }}>Team 1</th>
              <th style={{ textAlign: 'left' }}>Team 2</th>
            </tr>
          </thead>
          <tbody>
            {games.map((game, index) => (
              <tr key={index} style={{ borderBottom: '1px solid #ccc' }}>
                <td>{game.date}</td>
                <td>{game.time}</td>
                <td>{game.team1}</td>
                <td>{game.team2}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SpreadPage;
