import React, { useEffect, useState } from 'react';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedPicks, setSelectedPicks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:4000/api/games')
      .then((res) => res.json())
      .then((data) => {
        setGames(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching games:', error);
        setLoading(false);
      });
  }, []);

  const handlePick = (team) => {
    const alreadyPicked = selectedPicks.includes(team);

    if (alreadyPicked) {
      // Remove the team if already picked
      setSelectedPicks(selectedPicks.filter((t) => t !== team));
    } else if (selectedPicks.length < 10) {
      // Add the team if less than 10 picks
      setSelectedPicks([...selectedPicks, team]);
    } else {
      alert('You can only select up to 10 picks.');
    }
  };

  if (loading) {
    return <div className="p-4 text-lg">Loading games...</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Player Picks - Week 1</h1>
      <p className="mb-2">Selected Picks: {selectedPicks.length} / 10</p>
      <div className="grid gap-4">
        {games.map((game, index) => {
          const spread = parseFloat(game.spread);
          let displayText = '';
          if (spread < 0) {
            displayText = `${game.team1} (${spread}) vs ${game.team2}`;
          } else if (spread > 0) {
            displayText = `${game.team1} vs ${game.team2} (-${spread})`;
          } else {
            displayText = `${game.team1} vs ${game.team2}`;
          }

          return (
            <div key={index} className="border p-4 rounded shadow-md">
              <p className="text-lg font-medium mb-2">{displayText}</p>
              <div className="flex gap-4">
                {[game.team1, game.team2].map((team) => (
                  <button
                    key={team}
                    onClick={() => handlePick(team)}
                    className={`px-4 py-2 rounded border ${
                      selectedPicks.includes(team)
                        ? 'bg-blue-500 text-white font-bold'
                        : 'bg-gray-100 hover:bg-gray-200'
                    }`}
                  >
                    {team}
                  </button>
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default PlayerPicksPage;
