import React, { useState } from 'react';
import axios from 'axios';

function AdminUploadTool() {
  const [authorized, setAuthorized] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const [games, setGames] = useState([]);
  const [successMessage, setSuccessMessage] = useState('');

  const correctPassword = 'letmein';

  const handlePasswordSubmit = () => {
    if (passwordInput === correctPassword) {
      setAuthorized(true);

      // ⛳ Placeholder games for testing (replace with real Excel import later)
      setGames([
        {
          date: "Thu 9/5",
          time: "8:20 PM",
          team1: "Chiefs",
          team1Spread: "-7.5",
          team2: "Lions",
          team2Spread: "+7.5"
        },
        {
          date: "Sun 9/8",
          time: "1:00 PM",
          team1: "Bengals",
          team1Spread: "-3",
          team2: "Browns",
          team2Spread: "+3"
        }
      ]);
    } else {
      alert('Incorrect password. Try again.');
    }
  };

  const handleUploadToApprove = async () => {
    try {
      await axios.post('http://localhost:4000/api/uploadedGames', games);
      setSuccessMessage('✅ Uploaded and ready for approval!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('❌ Error uploading game data');
    }
  };

  if (!authorized) {
    return (
      <div className="p-4">
        <h2 className="text-xl font-bold mb-2">Admin Access</h2>
        <input
          type="password"
          placeholder="Enter admin password"
          className="border p-2 mr-2"
          value={passwordInput}
          onChange={(e) => setPasswordInput(e.target.value)}
        />
        <button
          onClick={handlePasswordSubmit}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Enter
        </button>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Admin: Upload Weekly Games</h2>

      <button
        onClick={handleUploadToApprove}
        className="bg-green-600 text-white px-4 py-2 rounded mb-4"
      >
        Save Games for Approval
      </button>

      {successMessage && (
        <div className="bg-green-100 text-green-700 border border-green-400 p-2 rounded mb-2">
          {successMessage}
        </div>
      )}

      <h3 className="font-bold mt-4">Preview Games:</h3>
      <ul className="list-disc pl-6">
        {games.map((game, index) => (
          <li key={index}>
            {game.date} – {game.time}: {game.team1} ({game.team1Spread}) vs {game.team2} ({game.team2Spread})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminUploadTo
