import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './IntroPage.css';
import americanFootballImage from '../assets/1201ff72-13e2-42a4-996a-4c43b208b0a6.jpeg'; // Make sure this is in /src/assets

function IntroPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    if (
      (username === 'Popcorn' && password === '&Peanuts!') ||
      (username === 'Admin' && password === 'letmein')
    ) {
      const route = username === 'Admin' ? '/admin' : '/picks';
      navigate(route);
    } else {
      setError('Incorrect username or password');
    }
  };

  return (
    <div className="intro-container">
      <h1 className="intro-title">Welcome to Shama-Lama Football Challenge</h1>
      <img
        src={americanFootballImage}
        alt="American flag football"
        className="intro-football-image"
      />

      <div className="login-box">
        <h2>Enter Game Name and PIN</h2>
        <input
          type="text"
          placeholder="Game Name"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="PIN"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Enter</button>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
}

export default IntroPage;
