import React, { useState } from 'react';
import PlayerPicksPage from './PlayerPicksPage';
import AllPlayersPicksPage from './AllPlayersPicksPage';
import SpreadPage from './SpreadPage';
import WeeklyScoresPage from './WeeklyScoresPage';
import RosterPaymentsPage from './RosterPaymentsPage';
import AdminToolsPage from './AdminToolsPage';
import RulesPage from './RulesPage';
import ChatPage from './ChatPage';

const IntroPage = () => {
  const [selectedTab, setSelectedTab] = useState('Player Picks');

  const renderTab = () => {
    switch (selectedTab) {
      case 'Player Picks':
        return <PlayerPicksPage />;
      case 'All Picks':
        return <AllPlayersPicksPage />;
      case 'Spread':
        return <SpreadPage />;
      case 'Scores':
        return <WeeklyScoresPage />;
      case 'Roster':
        return <RosterPaymentsPage />;
      case 'Admin':
        return <AdminToolsPage />;
      case 'Rules':
        return <RulesPage />;
      case 'Chat':
        return <ChatPage />;
      default:
        return <PlayerPicksPage />;
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome to the Football Pick'em Game</h1>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => setSelectedTab('Player Picks')}>Place Your Picks Here</button>
        <button onClick={() => setSelectedTab('All Picks')}>All Players' Picks Page</button>
        <button onClick={() => setSelectedTab('Spread')}>The Spread by Week</button>
        <button onClick={() => setSelectedTab('Scores')}>Weekly Scores</button>
        <button onClick={() => setSelectedTab('Roster')}>Player Roster & Payments</button>
        <button onClick={() => setSelectedTab('Admin')}>Administration Tools</button>
        <button onClick={() => setSelectedTab('Rules')}>Rules</button>
        <button onClick={() => setSelectedTab('Chat')}>Chat</button>
      </div>
      {renderTab()}
    </div>
  );
};

export default IntroPage;
