import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const PlayerPicksPage = () => {
  const { week } = useParams();
  const [games, setGames] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [selectedPicks, setSelectedPicks] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch(`http://localhost:4000/api/games/${week}`)
      .then(res => res.json())
      .then(data => {
        console.log("Fetched games:", data);
        setGames(data);
      })
      .catch(err => console.error('Error loading games:', err));
  }, [week]);

  const togglePick = (team) => {
    setSelectedPicks(prev =>
      prev.includes(team)
        ? prev.filter(p => p !== team)
        : [...prev, team]
    );
  };

  const handleSubmit = () => {
    if (selectedPicks.length !== 10) {
      setMessage('You must select exactly 10 teams.');
      return;
    }

    if (!playerName.trim()) {
      setMessage('Please enter your exact game name.');
      return;
    }

    fetch(`http://localhost:4000/api/picks/${week}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ player: playerName.trim(), picks: selectedPicks }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setMessage('Picks submitted successfully!');
        } else {
          setMessage('Error submitting picks.');
        }
      })
      .catch(err => {
        console.error(err);
        setMessage('Error submitting picks.');
      });
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Make Your Picks - Week {week}</h1>

      <Link
        to={`/print/week/${week}`}
        className="inline-block mb-4 px-4 py-2 bg-blue-100 text-blue-800 rounded shadow hover:bg-blue-200"
      >
        üñ®Ô∏è Print This Week's Games
      </Link>

      <div className="mb-4">
        <input
          type="text"
          placeholder="Enter your game name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded"
        />
      </div>

      <div className="grid gap-4">
        {Array.isArray(games) && games.map((game, idx) => {
          const spread = parseFloat(game.spread);
          const isFavorite = spread < 0;
          const favorite = isFavorite ? game.team1 : game.team2;
          const underdog = isFavorite ? game.team2 : game.team1;
          const displaySpread = Math.abs(spread).toFixed(1);

          const pickOptions = [
            { team: favorite, spread: `-${displaySpread}` },
            { team: underdog, spread: `+${displaySpread}` }
          ];

          return (
            <div key={idx} className="border p-3 rounded shadow">
              {pickOptions.map((option) => (
                <button
                  key={option.team}
                  onClick={() => togglePick(option.team)}
                  className={`block w-full text-left p-2 my-1 border rounded ${
                    selectedPicks.includes(option.team)
                      ? 'bg-green-100 border-green-400'
                      : 'bg-white border-gray-300'
                  }`}
                >
                  {option.team} ({option.spread})
                  {selectedPicks.includes(option.team) && <span className="ml-2 text-green-600 font-bold">Picked</span>}
                </button>
              ))}
            </div>
          );
        })}
      </div>

      <div className="mt-4">
        <button
          onClick={handleSubmit}
          className="px-6 py-2 bg-blue-600 text-white rounded shadow hover:bg-blue-700"
        >
          Submit Picks
        </button>
      </div>

      {message && <p className="mt-4 text-center text-red-600">{message}</p>}
    </div>
  );
};

export default PlayerPicksPage;
