// src/components/PrintableGamesPage.jsx

import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import printIcon from '../assets/print-icon.png';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const contentRef = useRef();

  useEffect(() => {
    fetch(`http://localhost:4000/api/games/${weekNumber}`)
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error('Error fetching games:', err));
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    if (contentRef.current) {
      html2pdf()
        .from(contentRef.current)
        .save(`Week_${weekNumber}_Games.pdf`);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Printable Game List - Week {weekNumber}</h2>

      <label htmlFor="weekSelector">Select Week: </label>
      <select id="weekSelector" onChange={handleWeekChange} value={weekNumber}>
        {Array.from({ length: 18 }, (_, i) => (
          <option key={i + 1} value={i + 1}>
            Week {i + 1}
          </option>
        ))}
      </select>

      <div style={{ margin: '10px 0' }}>
        <button onClick={handlePrint} style={{ cursor: 'pointer', marginRight: '10px' }}>
          <img src={printIcon} alt="Print Icon" width="20" style={{ verticalAlign: 'middle' }} /> Export to PDF
        </button>
        <button onClick={() => window.print()} style={{ cursor: 'pointer' }}>
          üñ®Ô∏è Print This Page
        </button>
      </div>

      <div ref={contentRef} style={{ marginTop: '20px' }}>
        {games.length === 0 ? (
          <p>No games found for Week {weekNumber}.</p>
        ) : (
          <ul>
            {games.map((game, index) => (
              <li key={index}>
                {game.awayTeam} @ {game.homeTeam} ({game.spread})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PrintableGamesPage;
