import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const [availableWeeks, setAvailableWeeks] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState(weekNumber || '1');

  useEffect(() => {
    fetch(`/api/weeks`)
      .then((res) => res.json())
      .then((weeks) => {
        setAvailableWeeks(weeks);
      })
      .catch((err) => console.error('Error loading weeks:', err));
  }, []);

  useEffect(() => {
    fetch(`/api/games/${selectedWeek}`)
      .then((res) => {
        if (!res.ok) throw new Error('No games found');
        return res.json();
      })
      .then((data) => {
        setGames(data);
      })
      .catch(() => setGames([]));
  }, [selectedWeek]);

  const handleWeekChange = (e) => {
    setSelectedWeek(e.target.value);
  };

  return (
    <div>
      <nav>
        <Link to="/picks">Player Picks</Link> |{' '}
        <Link to="/results">Results</Link> |{' '}
        <Link to="/print/week/1">Printable</Link> |{' '}
        <Link to="/admin-upload">Upload Games</Link> |{' '}
        <Link to="/admin-approve">Approve Games</Link>
      </nav>

      <h2>Printable Game List - Week {selectedWeek}</h2>

      <div>
        <label>Select Week: </label>
        <select value={selectedWeek} onChange={handleWeekChange}>
          {availableWeeks.map((week) => (
            <option key={week} value={week}>
              Week {week}
            </option>
          ))}
        </select>
        <button onClick={() => window.print()}>Print This Page</button>
      </div>

      {games.length === 0 ? (
        <p>No games found for Week {selectedWeek}.</p>
      ) : (
        <ul>
          {games.map((game, index) => (
            <li key={index}>
              <strong>{game.team1}</strong> ({game.team1Spread}) vs{' '}
              <strong>{game.team2}</strong> ({game.team2Spread})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PrintableGamesPage;
