import React, { useEffect, useState } from 'react';

const RulesPage = () => {
  const [rulesText, setRulesText] = useState('');

  useEffect(() => {
    fetch('/api/rules')
      .then(res => res.json())
      .then(data => {
        if (data.rulesText) {
          setRulesText(data.rulesText);
        } else if (data.message) {
          setRulesText(data.message);
        }
      })
      .catch(err => {
        console.error('Failed to load rules:', err);
        setRulesText('Unable to load rules at this time.');
      });
  }, []);

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', whiteSpace: 'pre-wrap', fontSize: '15px', lineHeight: '1.5' }}>
      {rulesText}
    </div>
  );
};

export default RulesPage;
