import React, { useEffect, useState } from 'react';

function PrintableGamesPage() {
  const [games, setGames] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState('1');

  useEffect(() => {
    if (!selectedWeek) return;

    fetch(`http://localhost:4000/api/games/week/${selectedWeek}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setGames(data))
      .catch((err) => {
        console.error('Error loading games:', err);
        setGames([]);
      });
  }, [selectedWeek]);

  const handlePrint = () => {
    window.print();
  };

  return (
    <div style={{ padding: '20px' }}>
      <nav style={{ marginBottom: '20px' }}>
        <a href="/picks">Player Picks</a> |{' '}
        <a href="/results">Results</a> |{' '}
        <a href="/print">Printable</a> |{' '}
        <a href="/upload">Upload Games</a> |{' '}
        <a href="/approve">Approve Games</a>
      </nav>

      <h2>Printable Game List - Week {selectedWeek}</h2>

      <div style={{ marginBottom: '10px' }}>
        <label>Select Week: </label>
        <select
          value={selectedWeek}
          onChange={(e) => setSelectedWeek(e.target.value)}
        >
          {[...Array(18)].map((_, i) => (
            <option key={i + 1} value={i + 1}>
              {i + 1}
            </option>
          ))}
        </select>
        <button onClick={handlePrint} style={{ marginLeft: '10px' }}>
          Print This Page
        </button>
      </div>

      {games.length === 0 ? (
        <p>No games found for Week {selectedWeek}.</p>
      ) : (
        <ul>
          {games.map((game, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              <strong>{game.date} â€“ {game.time}</strong><br />
              {game.team1} ({game.team1Spread}) vs {game.team2} ({game.team2Spread})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PrintableGamesPage;
