import React, { useEffect, useState } from 'react';

const AllPlayersPicksPage = () => {
  const [currentWeek, setCurrentWeek] = useState(1);
  const [playerData, setPlayerData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Step 1: Get current week from file
        const weekRes = await fetch('/data/current_week.json');
        const weekJson = await weekRes.json();
        const week = weekJson.week;
        setCurrentWeek(week);

        // Step 2: Get picks file for that week
        const picksRes = await fetch(`/data/picks_week_${week}.json`);
        const picksJson = await picksRes.json();
        setPlayerData(picksJson);
      } catch (err) {
        console.error('Error loading player picks:', err);
        setPlayerData([]);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div style={{ padding: '20px' }}>Loading All Player Picks...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        All Players' Picks - Week {currentWeek}
      </h2>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th style={thStyle}>Game Name</th>
            <th style={thStyle}>Picks</th>
            <th style={thStyle}>Winning Picks</th>
            <th style={thStyle}>Weekly Points</th>
            <th style={thStyle}>Cumulative</th>
          </tr>
        </thead>
        <tbody>
          {playerData.map((entry, idx) => (
            <tr key={idx}>
              <td style={tdStyle}>{entry.player || 'Unknown'}</td>
              <td style={tdStyle}>{entry.picks?.join(', ') || 'No picks submitted'}</td>
              <td style={tdStyle}>No data available until Tuesday</td>
              <td style={tdStyle}>N/A</td>
              <td style={tdStyle}>N/A</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const thStyle = {
  padding: '10px',
  borderBottom: '2px solid #ccc',
  textAlign: 'left'
};

const tdStyle = {
  padding: '8px',
  borderBottom: '1px solid #ddd',
  verticalAlign: 'top'
};

export default AllPlayersPicksPage;
