import React, { useState, useEffect } from 'react';
import axios from 'axios';

const isAfterDeadline = () => {
  const now = new Date();
  const deadline = new Date();
  deadline.setHours(12, 0, 0, 0); // 12:00 PM
  return now.getDay() > 5 || now >= deadline; // After Friday noon
};

const ViewPicksPage = () => {
  const [picksData, setPicksData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showPicks, setShowPicks] = useState(false);

  useEffect(() => {
    if (isAfterDeadline()) {
      axios.get('http://localhost:4000/api/picks')
        .then(res => {
          setPicksData(res.data);
          setShowPicks(true);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error fetching picks:', err);
          setLoading(false);
        });
    } else {
      setShowPicks(false);
      setLoading(false);
    }
  }, []);

  if (loading) return <div className="p-4">Loading picks...</div>;

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">All Player Picks</h2>

      {!showPicks && (
        <p className="text-orange-600 font-semibold">
          Picks will be viewable after the submission deadline (Friday 12:00 PM).
        </p>
      )}

      {showPicks && picksData.map((entry, index) => (
        <div key={index} className="mb-6 border-b pb-2">
          <h3 className="font-semibold">{entry.name}</h3>
          <ul className="list-disc list-inside">
            {entry.picks.map((team, i) => (
              <li key={i}>{team}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default ViewPicksPage;
