import React, { useEffect, useState } from 'react';

const ScoredPicksPage = () => {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/api/scores')
      .then(res => res.json())
      .then(data => setScores(data))
      .catch(err => console.error('Error fetching scores:', err));
  }, []);

  return (
    <div style={{ padding: '30px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ marginBottom: '20px' }}>Weekly Scored Picks</h2>
      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
      }}>
        <thead>
          <tr style={{ backgroundColor: '#f4f4f4', textAlign: 'left' }}>
            <th style={thStyle}>Player</th>
            <th style={thStyle}>Week</th>
            <th style={thStyle}>Picks</th>
            <th style={thStyle}>Correct Picks</th>
            <th style={thStyle}>Weekly Score</th>
            <th style={thStyle}>Total Score</th>
            <th style={thStyle}>‚≠ê</th>
            <th style={thStyle}>Praise</th>
          </tr>
        </thead>
        <tbody>
          {scores.map((player, index) => (
            <tr key={index} style={index % 2 === 0 ? rowEven : rowOdd}>
              <td style={tdStyle}>{player.name}</td>
              <td style={tdStyle}>{player.week}</td>
              <td style={tdStyle}>{player.picks.join(', ')}</td>
              <td style={tdStyle}>{player.correctPicks.join(', ')}</td>
              <td style={tdStyle}>{player.weeklyScore}</td>
              <td style={tdStyle}>{player.totalScore}</td>
              <td style={tdStyle}>{player.goldStar}</td>
              <td style={tdStyle}>{player.praise}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Styling objects
const thStyle = {
  padding: '10px',
  borderBottom: '2px solid #ddd',
  fontWeight: 'bold'
};

const tdStyle = {
  padding: '10px',
  borderBottom: '1px solid #eee',
  verticalAlign: 'top'
};

const rowEven = {
  backgroundColor: '#fff'
};

const rowOdd = {
  backgroundColor: '#f9f9f9'
};

export default ScoredPicksPage;
