import React, { useState } from 'react';

function AdminUploadPage() {
  const [status, setStatus] = useState('');

  const handleUpload = async (e) => {
    e.preventDefault();
    const fileInput = document.getElementById('fileInput');
    const file = fileInput.files[0];

    if (!file) {
      setStatus('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('spreadsheet', file);

    try {
      const response = await fetch('http://localhost:4000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setStatus(data.message || 'Smart file upload and conversion successful!');
      } else {
        setStatus(data.message || 'Upload failed. Please try again.');
      }
    } catch (error) {
      console.error('Upload error:', error);
      setStatus('Upload failed. Please try again.');
    }
  };

  return (
    <div>
      <h2>Admin Upload Page</h2>
      <form onSubmit={handleUpload}>
        <input type="file" id="fileInput" name="spreadsheet" accept=".xlsx, .xls" />
        <button type="submit">Upload File</button>
      </form>
      <p>{status}</p>
    </div>
  );
}

export default AdminUploadPage;
