import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const IntroPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [authenticated, setAuthenticated] = useState(false);
  const navigate = useNavigate();

  const handleLogin = () => {
    if (username === 'admin' && password === 'football') {
      setAuthenticated(true);
    } else {
      alert('Invalid credentials');
    }
  };

  if (!authenticated) {
    return (
      <div style={{ padding: '40px' }}>
        <h2>Football Pick'em Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        /><br />
        <button onClick={handleLogin}>Login</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Welcome to the Football Pick'em</h2>
      <nav>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li><a href="/picks">1. Place Your Picks Here</a></li>
          <li><a href="/allpicks">2. All Players' Picks Page</a></li>
          <li><a href="/spread">3. The Spread by Week</a></li>
          <li><a href="/weekly-scores">4. Weekly Scores</a></li>
          <li><a href="/roster-payments">5. Player Roster & Payments</a></li>
          <li><a href="/admin-tools">6. Administration Tools</a></li>
          <li><a href="/rules">7. Rules</a></li>
          <li><a href="/chat">8. Chat</a></li>
        </ul>
      </nav>
    </div>
  );
};

export default IntroPage;
