import React, { useState, useEffect } from 'react';

const ScoredPicksPage = () => {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    fetch('/api/scores/all')
      .then(res => res.json())
      .then(data => setScores(data))
      .catch(err => console.error('Error fetching all scores:', err));
  }, []);

  const allWeeks = [...new Set(scores.flatMap(player => Object.keys(player.weekly || {})))].sort((a, b) => parseInt(a) - parseInt(b));

  return (
    <div>
      <h1>Leaderboard</h1>
      <table style={{ fontSize: '18px', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr>
            <th style={{ textAlign: 'left', paddingRight: '20px' }}>Player</th>
            {allWeeks.map(week => (
              <th key={week}>Wk {week}</th>
            ))}
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {scores.map((player, index) => (
            <tr key={index}>
              <td>{player.name}</td>
              {allWeeks.map(week => (
                <td key={week} style={{ textAlign: 'center' }}>
                  {player.weekly[week] || 0}
                </td>
              ))}
              <td style={{ fontWeight: 'bold' }}>{player.total}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ScoredPicksPage;
