import React, { useState, useEffect } from 'react';

function PlayerPicksPage() {
  const [week, setWeek] = useState(null);
  const [games, setGames] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [pin, setPin] = useState('');
  const [picks, setPicks] = useState({});
  const [status, setStatus] = useState('auth'); // 'auth', 'form', 'alreadyPicked', 'submitted', 'locked'
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('/data/current_week.json')
      .then(res => res.json())
      .then(data => setWeek(data.currentWeek));
  }, []);

  useEffect(() => {
    if (week) {
      fetch(`/data/games_week_${week}.json`)
        .then(res => res.json())
        .then(data => setGames(data));
    }
  }, [week]);

  const isLockedOut = () => {
  // üö´ Temporarily disabled for testing
  // const now = new Date();
  // const day = now.getDay(); // Sunday = 0, Thursday = 4
  // const hour = now.getHours();
  // const minutes = now.getMinutes();
  // return (day > 4) || (day === 4 && (hour > 13 || (hour === 13 && minutes >= 0)));

  return false; // ‚úÖ Always unlocked during testing
  };

  const handleAuthenticate = () => {
    const trimmedName = playerName.trim();

    if (isLockedOut()) {
      setStatus('locked');
      return;
    }

    fetch('/api/authenticate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ gameName: trimmedName, pin })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          checkIfAlreadyPicked(trimmedName);
        } else {
          setMessage('Invalid Game Name or PIN.');
        }
      })
      .catch(() => setMessage('Error verifying credentials.'));
  };

  const checkIfAlreadyPicked = (trimmedName) => {
    fetch('/api/check-player-picks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ week, playerName: trimmedName })
    })
      .then(res => res.json())
      .then(data => {
        if (data.alreadyPicked) {
          setStatus('alreadyPicked');
        } else {
          setStatus('form');
        }
      })
      .catch(() => setMessage('Error checking player picks.'));
  };

  const handleTogglePick = (gameIndex, team) => {
    setPicks(prev => {
      if (prev[gameIndex] === team) {
        const newPicks = { ...prev };
        delete newPicks[gameIndex];
        return newPicks;
      }
      return { ...prev, [gameIndex]: team };
    });
  };

  const handleSubmit = () => {
    const pickArray = games.map((game, index) => ({
      gameIndex: index,
      pick: picks[index] || null
    }));

    fetch(`/submit-picks/${week}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        player: playerName.trim(),
        pin,
        picks: pickArray
      })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setStatus('submitted');
          setPicks({});
        } else {
          setMessage(data.error || 'Failed to submit picks.');
        }
      })
      .catch(() => setMessage('Error submitting picks.'));
  };

  if (!week) return <div>Loading week...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Place Your Picks</h2>

      {status === 'auth' && (
        <div>
          <p>Enter your Game Name and PIN:</p>
          <input
            type="text"
            placeholder="Game Name"
            value={playerName}
            onChange={e => setPlayerName(e.target.value)}
          />
          <input
            type="password"
            placeholder="PIN"
            value={pin}
            onChange={e => setPin(e.target.value)}
          />
          <button onClick={handleAuthenticate}>Login</button>
          <p style={{ color: 'red' }}>{message}</p>
        </div>
      )}

      {status === 'locked' && (
        <div style={{ color: 'darkred', fontWeight: 'bold', fontSize: '18px', marginTop: '20px' }}>
          üîí Picks are now locked. You may return after the week ends to see results.
        </div>
      )}

      {status === 'alreadyPicked' && (
        <div style={{ color: 'green', fontWeight: 'bold', fontSize: '18px' }}>
          ‚úÖ Your picked teams have already been placed for this week.
        </div>
      )}

      {status === 'submitted' && (
        <div style={{ marginTop: '40px', fontSize: '18px', color: '#003366' }}>
          <p><strong>‚úÖ Your picks for Week {week} have been saved.</strong></p>
          <p>Please visit the <strong>All Players Picks Page</strong> to see your picks and everyone else‚Äôs.</p>
          <p style={{ fontStyle: 'italic', color: 'green' }}>Good luck!</p>
        </div>
      )}

      {status === 'form' && (
        <div>
          <h3>Week {week} Games</h3>
          <p>Select your teams (up to 10):</p>
          {games.map((game, index) => (
            <div key={index} style={{ marginBottom: '12px', background: '#f9f9f9', padding: '10px', borderRadius: '8px' }}>
              <strong>{game.date || ''} ‚Äî {game.time || ''}</strong><br />
              <button
                onClick={() => handleTogglePick(index, game.team1)}
                style={{
                  backgroundColor: picks[index] === game.team1 ? 'yellow' : '',
                  border: '1px solid black',
                  marginRight: '8px',
                  padding: '5px 10px',
                  cursor: 'pointer',
                  fontWeight: 'bold'
                }}
              >
                {game.team1_spread ? `${game.team1_spread} ` : ''}{game.team1}
              </button>
              {'vs'}
              <button
                onClick={() => handleTogglePick(index, game.team2)}
                style={{
                  backgroundColor: picks[index] === game.team2 ? 'yellow' : '',
                  border: '1px solid black',
                  marginLeft: '8px',
                  padding: '5px 10px',
                  cursor: 'pointer',
                  fontWeight: 'bold'
                }}
              >
                {game.team2_spread ? `${game.team2_spread} ` : ''}{game.team2}
              </button>
            </div>
          ))}

          <div style={{ marginTop: '20px' }}>
            <p style={{ color: 'blue' }}>
              You have selected {Object.keys(picks).length} out of 10 games.
            </p>
            <button onClick={handleSubmit} style={{ backgroundColor: 'green', color: 'white', padding: '10px 20px' }}>
              Submit Picks
            </button>
            <p style={{ color: 'darkred', marginTop: '10px', fontWeight: 'bold' }}>
              ‚ö†Ô∏è Please double-check picks before submitting. Submission is final.
            </p>
            <p>
              You will be able to see your selections on the <strong>All Players Picks Page</strong>. <br />
              Winning picks and scores will be revealed on Tuesday.
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default PlayerPicksPage;
