// src/components/AdminApproveGamesPage.jsx
import React, { useState, useEffect } from 'react';

const AdminApproveGamesPage = () => {
  const [games, setGames] = useState([]);
  const [week, setWeek] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('/api/uploaded-games')
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error('Failed to fetch uploaded games:', err));
  }, []);

  const handleApprove = () => {
    if (!week || isNaN(week)) {
      setMessage('Please enter a valid week number.');
      return;
    }

    fetch('/api/approve-games', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ week }),
    })
      .then((res) => res.json())
      .then((data) => {
        setMessage(data.message);
        setGames([]); // clear the view after success
      })
      .catch((err) => {
        console.error('Error approving games:', err);
        setMessage('An error occurred while saving games.');
      });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Approve Uploaded Games</h2>

      <label>
        Week Number:{' '}
        <input
          type="number"
          value={week}
          onChange={(e) => setWeek(e.target.value)}
          style={{ width: '60px' }}
        />
      </label>

      <button onClick={handleApprove} style={{ marginLeft: '10px' }}>
        Approve and Save
      </button>

      <p>{message}</p>

      {games.length === 0 ? (
        <p>No games uploaded yet.</p>
      ) : (
        <table border="1" cellPadding="6" style={{ marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Date</th>
              <th>Time</th>
              <th>Team 1</th>
              <th>Spread 1</th>
              <th>Team 2</th>
              <th>Spread 2</th>
            </tr>
          </thead>
          <tbody>
            {games.map((game, index) => (
              <tr key={index}>
                <td>{game.date}</td>
                <td>{game.time}</td>
                <td>{game.team1}</td>
                <td>{game.team1Spread}</td>
                <td>{game.team2}</td>
                <td>{game.team2Spread}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminApproveGamesPage;
