import React, { useState, useEffect } from 'react';

const PlayerPicksPage = () => {
  const [gameName, setGameName] = useState('');
  const [pin, setPin] = useState('');
  const [authenticated, setAuthenticated] = useState(false);
  const [games, setGames] = useState([]);
  const [picks, setPicks] = useState({});
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const MAX_PICKS = 10;

  // Load game spread after authentication
  useEffect(() => {
    if (authenticated) {
      fetch('/data/games_week_1.json')
        .then(res => res.json())
        .then(data => setGames(data))
        .catch(() => setError('Could not load game spread.'));
    }
  }, [authenticated]);

  // Handle Game Name + PIN submission
  const handleAuth = async () => {
    setError('');
    try {
      const response = await fetch('http://localhost:4000/api/authenticate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ gameName, pin })
      });
      const data = await response.json();

      if (response.ok) {
        setAuthenticated(true);
        setMessage(`Welcome, ${gameName}! Balance: ${data.balance}`);
      } else {
        setError(data.error || 'Authentication failed.');
      }
    } catch (err) {
      setError('Server error during authentication.');
    }
  };

  // Handle pick selection (radio buttons)
  const handlePick = (gameIndex, pickValue) => {
    const newPicks = { ...picks, [gameIndex]: pickValue };

    const totalSelected = Object.keys(newPicks).length;
    if (totalSelected > MAX_PICKS) {
      alert(`You can only select ${MAX_PICKS} teams.`);
      return;
    }
    setPicks(newPicks);
  };

  const handleSubmit = () => {
    const total = Object.keys(picks).length;
    if (total !== MAX_PICKS) {
      alert(`You must select exactly ${MAX_PICKS} teams.`);
      return;
    }

    // Here you would send picks to the backend, e.g.:
    // fetch('/api/submit-picks', ...)
    console.log('Submitted picks:', picks);
    alert('Your picks have been submitted!');
  };

  return (
    <div style={{ padding: '30px', maxWidth: '800px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>Place Your Picks Here</h2>

      {!authenticated ? (
        <>
          <p>Please enter your Game Name and PIN to unlock this week’s games:</p>
          <input
            type="text"
            placeholder="Game Name"
            value={gameName}
            onChange={e => setGameName(e.target.value)}
            style={{ marginRight: '10px', padding: '5px' }}
          />
          <input
            type="password"
            placeholder="PIN"
            value={pin}
            onChange={e => setPin(e.target.value)}
            style={{ marginRight: '10px', padding: '5px' }}
          />
          <button onClick={handleAuth}>Unlock Picks</button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </>
      ) : (
        <>
          <p style={{ color: 'green' }}>{message}</p>
          <p style={{ fontWeight: 'bold', color: '#a00' }}>
            ⚠️ You are only allowed 10 picks. Picking more or less will result in an error.
          </p>

          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Time</th>
                <th>Team 1</th>
                <th>Team 2</th>
                <th>Pick</th>
              </tr>
            </thead>
            <tbody>
              {games.map((game, index) => (
                <tr key={index} style={{ borderBottom: '1px solid #ccc' }}>
                  <td>{game.time}</td>
                  <td>{game.team1}</td>
                  <td>{game.team2}</td>
                  <td>
                    <label>
                      <input
                        type="radio"
                        name={`pick-${index}`}
                        value="team1"
                        checked={picks[index] === 'team1'}
                        onChange={() => handlePick(index, 'team1')}
                      />{' '}
                      {game.team1}
                    </label>
                    {'  '}
                    <label>
                      <input
                        type="radio"
                        name={`pick-${index}`}
                        value="team2"
                        checked={picks[index] === 'team2'}
                        onChange={() => handlePick(index, 'team2')}
                      />{' '}
                      {game.team2}
                    </label>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button style={{ marginTop: '20px' }} onClick={handleSubmit}>
            Submit Picks
          </button>
        </>
      )}
    </div>
  );
};

export default PlayerPicksPage;
