import React, { useEffect, useState } from 'react';
import './SpreadPage.css';

const SpreadPage = () => {
  const [games, setGames] = useState([]);
  const [week, setWeek] = useState(1);

  useEffect(() => {
    fetch('/data/current_week.json')
      .then(res => res.json())
      .then(data => {
        const w = data.currentweek || 1;
        setWeek(w);
        fetch(`/data/games_week_${w}.json`)
          .then(res => res.json())
          .then(data => setGames(data));
      });
  }, []);

  return (
    <div className="page-container">
      <h2>Game Spread - Week {week}</h2>
      <div style={{ marginBottom: '12px' }}>
        <button onClick={() => window.print()} style={{ marginRight: '10px' }}>üñ®Ô∏è Print This Page</button>
        <button onClick={() => window.print()}>üìÑ Export to PDF</button>
      </div>
      <table className="spread-table">
        <thead>
          <tr>
            <th>Date & Time</th>
            <th>Team 1</th>
            <th>Spread</th>
            <th>Team 2</th>
            <th>Spread</th>
          </tr>
        </thead>
        <tbody>
          {games.map((game, idx) => (
            <tr key={idx}>
              <td>{game.date || '-'}</td>
              <td>{game.team1}</td>
              <td>{game.spread1}</td>
              <td>{game.team2}</td>
              <td>{game.spread2}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SpreadPage;
