import React, { useEffect, useState } from 'react';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedPicks, setSelectedPicks] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [pin, setPin] = useState('');
  const [authorized, setAuthorized] = useState(false);
  const [message, setMessage] = useState('');
  const [currentWeek] = useState(1);

  useEffect(() => {
    fetch('/data/games_week_1.json')
      .then(res => res.json())
      .then(data => setGames(data))
      .catch(err => console.error('Error loading games:', err));
  }, []);

  const handlePick = (team) => {
    if (selectedPicks.includes(team)) {
      setSelectedPicks(selectedPicks.filter(t => t !== team));
    } else if (selectedPicks.length < 10) {
      setSelectedPicks([...selectedPicks, team]);
    }
  };

  const handleLogin = () => {
    fetch('/api/authenticate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ gameName: playerName, pin })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setAuthorized(true);
          setMessage('');
        } else {
          setMessage(data.error || 'Login failed.');
        }
      })
      .catch(() => setMessage('Error connecting to server.'));
  };

  const handleSubmit = () => {
    if (selectedPicks.length !== 10) {
      setMessage('You must select exactly 10 teams.');
      return;
    }

    fetch(`/submit-picks/${currentWeek}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        player: playerName.trim(),
        picks: selectedPicks
      })
    })
      .then(res => res.json())
      .then(data => setMessage(data.message || 'Picks submitted successfully!'))
      .catch(() => setMessage('Error submitting picks.'));
  };

  if (!authorized) {
    return (
      <div className="p-4 max-w-md mx-auto">
        <h2 className="text-xl font-bold mb-4">Enter to Make Your Picks</h2>
        <input
          type="text"
          placeholder="Game Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="block mb-2 w-full px-3 py-2 border rounded"
        />
        <input
          type="password"
          placeholder="PIN"
          value={pin}
          onChange={(e) => setPin(e.target.value)}
          className="block mb-4 w-full px-3 py-2 border rounded"
        />
        <button
          onClick={handleLogin}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Login
        </button>
        {message && <div className="mt-4 text-red-600">{message}</div>}
      </div>
    );
  }

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Place Your Picks - Week {currentWeek}</h2>

      {games.map((game, idx) => (
        <div key={idx} className="border-b pb-3 mb-3">
          <div className="text-sm text-gray-600 mb-1">
            {game.date} — {game.time}
          </div>
          <div className="flex gap-8">
            {[{ team: game.team1, spread: game.team1_spread }, { team: game.team2, spread: game.team2_spread }].map(({ team, spread }) => (
              <label key={team} className="flex items-center gap-2">
                <input
                  type="radio"
                  name={`game-${idx}`}
                  value={team}
                  checked={selectedPicks.includes(team)}
                  onChange={() => handlePick(team)}
                />
                <span className="whitespace-nowrap">{spread} {team}</span>
              </label>
            ))}
          </div>
        </div>
      ))}

      <div className="text-sm font-medium text-blue-700 mt-4">
        You have selected {selectedPicks.length} out of 10 games.
      </div>

      <button
        onClick={handleSubmit}
        className="mt-4 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
      >
        Submit Picks
      </button>

      <div className="mt-3 text-yellow-800 font-medium">
        Please double-check picks before submitting. Submission is final.
      </div>

      <div className="text-sm text-gray-600 mt-2">
        You will be able to see your selections on the <strong>All Players Picks</strong> page.<br />
        Winning picks and scores will be revealed on Tuesday.
      </div>

      {message && <div className="mt-4 text-green-700 font-semibold">{message}</div>}
    </div>
  );
};

export default PlayerPicksPage;
