import React, { useState } from "react";
import * as XLSX from "xlsx";

const AdminUploadTool = () => {
  const [games, setGames] = useState([]);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const raw = XLSX.utils.sheet_to_json(sheet, { header: 1 });

    const flat = raw.flat().filter((val) => val !== undefined && val !== null);
    const gameList = [];

    for (let i = 0; i < flat.length - 10; i++) {
      if (typeof flat[i] === "string" && flat[i].match(/^\w{3} \d{1,2}\//)) {
        try {
          const [_, dateRaw] = flat[i].split(" ");
          const date = `Aug ${dateRaw.split("/")[1]}`;
          const time = typeof flat[i + 1] === "string" ? flat[i + 1] : "";
          const teamA = flat[i + 3];
          const spreadA = flat[i + 7]?.toString().replace("½", ".5") || "";
          const teamB = flat[i + 5];
          const spreadB = flat[i + 9]?.toString().replace("½", ".5") || "";

          if (teamA && teamB && spreadA && spreadB) {
            gameList.push({ date, time, teamA, spreadA, teamB, spreadB });
          }
          i += 4;
        } catch (err) {
          continue;
        }
      }
    }

    setGames(gameList);
  };

  const handleSave = () => {
    console.log("Saved Games:", games);
    alert("Games saved to backend (console only for now)");
  };

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Admin Upload: Weekly Game List</h1>
      <input
        type="file"
        accept=".xlsx"
        onChange={handleFileUpload}
        className="mb-4"
      />

      {games.length > 0 && (
        <>
          <table className="table-auto w-full border mb-4">
            <thead>
              <tr>
                <th>Date</th>
                <th>Time</th>
                <th>Team A</th>
                <th>Spread A</th>
                <th>Team B</th>
                <th>Spread B</th>
              </tr>
            </thead>
            <tbody>
              {games.map((game, idx) => (
                <tr key={idx}>
                  <td>{game.date}</td>
                  <td>{game.time}</td>
                  <td>{game.teamA}</td>
                  <td>{game.spreadA}</td>
                  <td>{game.teamB}</td>
                  <td>{game.spreadB}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button
            onClick={handleSave}
            className="bg-green-600 text-white px-4 py-2 rounded"
          >
            Save Week
          </button>
        </>
      )}
    </div>
  );
};

export default AdminUploadTool;
