// PlayerPicksPage.jsx
import React, { useEffect, useState } from 'react';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedPicks, setSelectedPicks] = useState({});

  useEffect(() => {
    fetch('http://localhost:4000/data/games_week_1.json')
      .then(res => res.json())
      .then(data => setGames(data))
      .catch(() => setGames([]));
  }, []);

  const handlePickChange = (gameIndex, team) => {
    setSelectedPicks(prev => ({ ...prev, [gameIndex]: team }));
  };

  const handleSubmit = () => {
    const picksArray = Object.values(selectedPicks);
    if (picksArray.length !== 10) {
      alert('You must select exactly 10 picks.');
      return;
    }
    alert('Picks submitted: ' + JSON.stringify(selectedPicks, null, 2));
  };

  return (
    <div style={{ padding: '30px', maxWidth: '1000px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>Place Your Picks Here</h2>
      <p style={{ color: 'green' }}>Welcome, masterblaster!</p>
      <p style={{ color: 'red', fontWeight: 'bold' }}>⚠️ You are only allowed 10 picks. Picking more or less will result in an error.</p>

      <table style={{ width: '100%', borderCollapse: 'collapse', marginBottom: '20px' }}>
        <thead>
          <tr>
            <th>Date</th>
            <th>Time</th>
            <th>Team 1</th>
            <th>Team 2</th>
            <th>Pick</th>
          </tr>
        </thead>
        <tbody>
          {games.map((game, index) => (
            <tr key={index}>
              <td>{game.date}</td>
              <td>{game.time}</td>
              <td>{game.team1} {game.team1_spread ? `(${game.team1_spread})` : ''}</td>
              <td>{game.team2} {game.team2_spread ? `(${game.team2_spread})` : ''}</td>
              <td>
                <label>
                  <input
                    type="radio"
                    name={`pick-${index}`}
                    value={game.team1}
                    checked={selectedPicks[index] === game.team1}
                    onChange={() => handlePickChange(index, game.team1)}
                  /> {game.team1}
                </label>
                &nbsp;&nbsp;
                <label>
                  <input
                    type="radio"
                    name={`pick-${index}`}
                    value={game.team2}
                    checked={selectedPicks[index] === game.team2}
                    onChange={() => handlePickChange(index, game.team2)}
                  /> {game.team2}
                </label>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <p style={{ color: 'red', fontStyle: 'italic' }}>
        ⚠️ Double-check your picks before submitting — once submitted, there's no turning back!
      </p>

      <button onClick={handleSubmit}>Submit Picks</button>
    </div>
  );
};

export default PlayerPicksPage;
