import React, { useState } from 'react';

const AdminToolsPage = () => {
  const [files, setFiles] = useState({
    spread: null,
    scores: null,
    roster: null,
    rules: null,
  });

  const handleFileChange = (event, key) => {
    const file = event.target.files[0];
    setFiles(prev => ({ ...prev, [key]: file }));
  };

  const handleImport = async (key, endpoint) => {
    const file = files[key];
    if (!file) {
      alert('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch(`http://localhost:4000${endpoint}`, {
        method: 'POST',
        body: formData
      });

      const data = await response.json();
      alert(data.message || 'Upload complete!');
    } catch (error) {
      console.error('Upload failed:', error);
      alert('Failed to upload file.');
    }
  };

  return (
    <div className="admin-tools" style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h2>Administration Tools</h2>

      {/* Game Spread Upload */}
      <div style={{ marginBottom: '20px' }}>
        <label><strong>Upload Game Spread</strong></label><br />
        <input type="file" onChange={(e) => handleFileChange(e, 'spread')} />
        <button onClick={() => handleImport('spread', '/upload-spread')}>Import File to JSON</button>
      </div>

      {/* Game Scores Upload */}
      <div style={{ marginBottom: '20px' }}>
        <label><strong>Upload Game Scores</strong></label><br />
        <input type="file" onChange={(e) => handleFileChange(e, 'scores')} />
        <button onClick={() => handleImport('scores', '/upload-scores')}>Import File to JSON</button>
      </div>

      {/* Player Roster Upload */}
      <div style={{ marginBottom: '20px' }}>
        <label><strong>Upload Player Roster</strong></label><br />
        <input type="file" onChange={(e) => handleFileChange(e, 'roster')} />
        <button onClick={() => handleImport('roster', '/upload-roster')}>Import File to JSON</button>
      </div>

      {/* Rules Document Upload */}
      <div style={{ marginBottom: '20px' }}>
        <label><strong>Upload Rules Document</strong></label><br />
        <input type="file" onChange={(e) => handleFileChange(e, 'rules')} />
        <button onClick={() => handleImport('rules', '/upload-rules')}>Import File to JSON</button>
      </div>
    </div>
  );
};

export default AdminToolsPage;
